import ShopActionTypes from "./shop.types";

const INITIAL_STATE = {
  collections: null,
  isFetching: false,
  errorMessage: ''
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.FETCH_COLLACTIONS_START:
      return {
        ...state,
        isFetching: true
      }

    case ShopActionTypes.FETCH_COLLACTIONS_SUCCESS:
      return {
        ...state,
        isFetching: false,
        collections: action.payload
      }

    case ShopActionTypes.FETCH_COLLACTIONS_FAILURE:
      return {
        ...state,
        isFetching: false,
        errorMessage: action.payload
      }

    case ShopActionTypes.UPDATE_COLLECTIONS:
      return {
        ...state,
        collections: action.payload,
      }

    default:
      return state;
  }
};

export default shopReducer;
