    componentDidMount() {
        const collectionRef = firestore.collection("collections");
        collectionRef.onSnapshot(async (snapshot) => {
            await console.log(snapshot)
        });
      }

  render() {

    return (
      <div className="order">
        <h2>ORDER # 123123123123</h2>
        <div className='order-area'>
            <div>
            <OrderItem/>
