import React from "react";

import OrderItem from "../order-item/order-item.component";

import "./order.styles.scss";

const Order = ({ dataItem }) => {
  const {
    id,
    date,
    items,
    name,
    location,
    city,
    country,
    zip,
    price,
  } = dataItem;
  const newDate = new Date(date).toLocaleDateString("en-US");
  const newTime = new Date(1504095567183).toLocaleTimeString("en-US");
  return (
    <div className="order">
      <h2 className="order-title">ORDER # {id}</h2>
      <h2>{newDate}, {newTime}</h2>
      <div className="order-area">
        <div>
          {items.map((item) => (
            <OrderItem cartItems={item} key={item.id} />
          ))}
        </div>
        <div className="item-details">
          <div>{name}</div>
          <div className="address">
            <span>{location}</span>
            <span>{city}</span>
            <span>{country}</span>
            <span>{zip}</span>
          </div>
          <span>{price}</span>
        </div>
      </div>
    </div>
  );
};

export default Order;
